package datatypelearning;
 
public class datatype {
 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//string na ""double codes & char na ''single codes
		String name="keerthi";
		System.out.println(name);//used bcz name error vanthuchi like engayume use pannalanu so we are using s.o.p(name)
		byte age = 24;//values mention panrathu
		System.out.println(age);
		float weight = 39.5F;//points la value mention pannanu,f end la mention panu ilana error adikum,(bcz java float value double consider paniurum f ilana)
		System.out.println(weight);
		double height = 157.6;//points la valuse mention pannanum. 
		System.out.println(height);
		long phonenumber = 1234567890l;//number la values mention panum with in some limits .limites thandi pochuna chinna number kudukanym
		System.out.println(phonenumber);
		int number=12345;//range athigam ana error varum so kami ana number kudu with in the limit
		System.out.println(number);
		short salary = 10000;//range athigam ana error varum so kami ana number kudu with in the limit
		System.out.println(salary);
		char initial ='M';// char na oru letter or number or yetha oru symbols iruthaum onu than store panna mudium athu single codes la kudukanum
		System.out.println(initial);
		boolean liked = true; //always true or false ethu tha  use pannamuidum
		System.out.println(liked);
	}
 
}

